class SensorResourcePool {
  Stack<Sensor> _availableSensors;
  Stack<Sensor> _allocatedSensors;

  Sensor getSensor() {
    Sensor sensor;

    if (_availableSensors.isEmpty()) {
      sensor = new Sensor();
    } else {
      sensor = _availableSensors.pop();
    }
    _allocatedSensors.push(sensor);
    return sensor;
  }
}
